con:configuration.scriptText:
import groovy.json.JsonSlurper 
import groovy.json.JsonOutput
try{
// Getting the response content.
def content = context.httpResponse.responseContent
// Process the content.
def jsonSlurper = new JsonSlurper()
def responseObj = jsonSlurper.parseText(content) 
def applicationResponseVal = responseObj.ServiceOutput 
def applicationResponseValUnescaped = applicationResponseVal.replaceAll("\\\\", "");
responseObj.ServiceOutput = jsonSlurper.parseText(applicationResponseValUnescaped)
// Overriding the response.
context.httpResponse.responseContent = JsonOutput.toJson(responseObj)
}
catch (e){
log.info("The response was not modified " + e)
}

